import React from 'react'
import { BrowserRouter, Switch, Routes } from 'react-router-dom'

//Chamando meus componentes Pages
import Home from '../Pages/Home'
import Sobre from '../Pages/Sobre'
import Portfolio from '../Pages/Portfolio'
import Contato from '../Pages/Contato'

function RoutesList() {
    return(
        //(BrowserRouter) Eu estou declarando que todas ás vezes que eu criar rotas no navegador vai ter que funcionar
        //(Switch) Estou dizendo que vai haver troca a cada página que for clicada
        //(Routes) Está sendo criado cada uma das navegações. Ou seja: React toda vez que alguém tiver na página que seja com final / path='/' eu quero que você execute o compoment= {Home} 
        //O exact: diz que toda vez que você encontrar somente a / mostre a home
        <BrowserRouter> 
            <Switch>
                <Routes path='/' exact components={Home}/>
                <Routes path='/Sobre' components={Sobre}/>
                <Routes path='/Portfolio' components={Portfolio}/>
                <Routes path='/Contato' components={Contato}/> 
            </Switch>
        </BrowserRouter>
    )

}

export default RoutesList
