import { useState, useEffect } from 'react'
import Datas from '../Services/Datas'

const Search = () => {

    const [busca, setBusca] = useState('') //1
    const [dados, setDados] = useState([])

    useEffect(()=>{
        async function getBooks(){
        const response = await Datas.get(`${busca}`)
            const data = await response.data

            if(busca!==''){
                setDados(data)
            }
        }
            getBooks()
    },[])


    return(
        <>
        <input type="text" onClick={e=>(setBusca(e.target.value))} placeholder='pesquise pelo título ou autora' className='input'/>
        </>
    )
}

export default Search
